          Contato
        </a></div></div><div class="footer-bottom" id="in18ce">© 2025 Meus Projetos. Todos os direitos reservados.</div></footer><script>
  (function(){
    const searchInput = document.getElementById('search');
    const resetBtn = document.querySelector('[data-gjs-name="Reset Filters Button"]');
    const categoryChecks = Array.from(document.querySelectorAll('[data-gjs-name="Category Checkbox"]'));
    const techChecks = Array.from(document.querySelectorAll('[data-gjs-name="Tech Checkbox"]'));
    const cards = Array.from(document.querySelectorAll('[data-gjs-name="Project Card"]'));
    const resultsCount = document.querySelector('[data-gjs-name="Results Count"]');

    function updateCount(visible){
      resultsCount.textContent = visible + ' projeto' + (visible === 1 ? '' : 's');
    }

    function getCheckedValues(nodes){
      return nodes.filter(n => n.checked).map(n => n.value.toLowerCase());
    }

    function matchesText(card, term){
      const title = card.querySelector('[data-gjs-name="Project Title"]').textContent.toLowerCase();
      const desc = card.querySelector('[data-gjs-name="Project Description"]').textContent.toLowerCase();
      return !term || title.includes(term) || desc.includes(term);
    }

    function matchesFacet(card, attr, selected){
      if (!selected.length) return true;
      const val = (card.getAttribute(attr) || '').toLowerCase();
      return selected.includes(val);
    }

    function applyFilters(){
      const term = (searchInput.value || '').trim().toLowerCase();
      const cats = getCheckedValues(categoryChecks);
      const techs = getCheckedValues(techChecks);
      let visible = 0;
      cards.forEach(card => {
        const okText = matchesText(card, term);
        const okCat = matchesFacet(card, 'data-category', cats);
        const okTech = matchesFacet(card, 'data-tech', techs);
        const show = okText && okCat && okTech;
        card.style.display = show ? '' : 'none';
        if (show) visible++;
      });
      updateCount(visible);
    }

    function initCustomCheckboxVisuals(){
      function sync(label){
        const input = label.querySelector('input[type="checkbox"]');
        const box = label.querySelector('[data-gjs-name$="Visual"]');
        if (!input || !box) return;
        if (input.checked){
          box.style.backgroundColor = '#2563eb';
          box.style.borderColor = '#2563eb';
          box.innerHTML = '<img alt="Marcado" src="https://api.iconify.design/lucide-check.svg?color=white" class="w-3 h-3" />';
        } else {
          box.style.backgroundColor = '#ffffff';
          box.style.borderColor = '#cbd5e1';
          box.innerHTML = '';
        }
      }
      const labels = Array.from(document.querySelectorAll('label[data-gjs-name$="Option"]'));
      labels.forEach(label => {
        const input = label.querySelector('input[type="checkbox"]');
        if (!input) return;
        sync(label);
        label.addEventListener('click', (e) => {
          if (e.target.tagName.toLowerCase() !== 'input'){
            input.checked = !input.checked;
            input.dispatchEvent(new Event('change', { bubbles: true }));
          }
        });
        input.addEventListener('change', () => {
          sync(label);
          applyFilters();
        });
        label.addEventListener('keydown', (e) => {
          if (e.key === 'Enter' || e.key === ' '){
            e.preventDefault();
            input.checked = !input.checked;
            input.dispatchEvent(new Event('change', { bubbles: true }));
          }
        });
        label.setAttribute('tabindex', '0');
        label.setAttribute('role', 'checkbox');
        label.setAttribute('aria-checked', input.checked ? 'true' : 'false');
        input.addEventListener('change', () => {
          label.setAttribute('aria-checked', input.checked ? 'true' : 'false');
        });
      });
    }

    searchInput.addEventListener('input', applyFilters);
    resetBtn.addEventListener('click', () => {
      searchInput.value = '';
      categoryChecks.forEach(c => c.checked = false);
      techChecks.forEach(t => t.checked = false);
      initCustomCheckboxVisuals();
      applyFilters();
    });

    initCustomCheckboxVisuals();
    applyFilters();
  })();
  </script></body>
